import React, { useState ,useEffect, useRef } from 'react';
import CircularProgress from '@material-ui/core/CircularProgress';
import Chart from 'chart.js/auto';
import 'chart.js/auto';

const PieChartEstado = () => {

  const [labels, setLabels] = useState(null);
  const [values, setValues] = useState(null);
  const [colors, setColors] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const chartRef = useRef();
  const chartInstance = useRef(null);

  const generateRandomColor = () => {
    // Generate a random hex color code
    const randomColor = '#' + Math.floor(Math.random()*16777215).toString(16);
    return randomColor;
  };
  
  useEffect(() => {

    const fetchData = async () => {
      try {
        const response = await fetch('http://127.0.0.1:5000/estados/Nível V'); // Replace with your API endpoint
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        const result = await response.json();
        let labels = Object.keys(result)
        let colors = []
        for(let i=0;i<labels.length;i++){
          colors.push(generateRandomColor())
        }
        setLabels(labels);
        setValues(Object.values(result))
        setColors(colors)
      } catch (error) {
        setError(error);
      } finally {
        setLoading(false);
      }
    };

    const ctx = chartRef.current.getContext('2d');

    // Destroy existing Chart instance if it exists
    if (chartInstance.current) {
      chartInstance.current.destroy();
    }

    // Create a new Chart instance
    chartInstance.current = new Chart(ctx, {
      type: 'pie',
      data: {
        labels: labels,
        datasets: [{
          data: values,
          backgroundColor: colors,
        }],
      },
    });

    fetchData();

    // Clean up on component unmount
    return () => {
      if (chartInstance.current) {
        chartInstance.current.destroy();
      }
    };
  }, []);

  const chartData = {
    labels: labels,
    values: values,
    colors: colors,
  };

  if (loading) {
    return(
      <div className="centered-container">
        <div className="centered-content">
          <CircularProgress/>;
        </div>
      </div>
    )
  }

  if (error) {
    return <p>Error: {error.message}</p>;
  }

  return (
    <div>
      <h1>Classificação INSE por estado</h1>
      <canvas ref={chartData} />;
    </div>
  )
    
    
};
  
export default PieChartEstado;
