import React, { useState, useEffect } from 'react';
import PieChart from './chart/piechart';
import { CircularProgress } from '@material-ui/core';
import './App.css'

const App = () => {

  const [chartDataEstados, setChartDataEstados] = useState(null);
  const [chartDataMunicipios, setChartDataMunicipios] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const generateRandomColor = () => {
    // Generate a random hex color code
    const randomColor = '#' + Math.floor(Math.random()*16777215).toString(16);
    return randomColor;
  };

  useEffect(() => {
    const fetchData = async () => {
      try {
        const responseEstados = await fetch('http://127.0.0.1:5000/estados/Nível V'); // Replace with your API endpoint
        const responseMunicipios = await fetch('http://127.0.0.1:5000/municipios/RO/Nível III'); // Replace with your API endpoint
        if (!responseEstados.ok || !responseMunicipios.ok) {
          throw new Error('Network response was not ok');
        }
        const resultEstados = await responseEstados.json();
        const resultMunicipios = await responseMunicipios.json();
        let colors = []
        for(let i=0;i<Object.keys(resultMunicipios).length;i++){
          colors.push(generateRandomColor())
        }
        setChartDataEstados({
          labels: Object.keys(resultEstados),
          values: Object.values(resultEstados),
          colors: colors,
        })
        setChartDataMunicipios({
          labels: Object.keys(resultMunicipios),
          values: Object.values(resultMunicipios),
          colors: colors,
        })
      } catch (error) {
        setError(error);
      } finally {
        setLoading(false);
      }

    };

    fetchData();
  }, []); 

  if (loading) {
    return(
      <div className="centered-container">
        <div className="centered-content">
          <CircularProgress/>
        </div>
      </div>
    )
  }

  if (error) {
    return <p>Error: {error.message}</p>;
  }

  return (
    <div className='centered-container'>
      <div className='centered-content'>
        <div className='row-container'>
          <div style={{width:500}}>
            <h1>Estados</h1>
            <PieChart data={chartDataEstados} />
          </div>
          <div style={{width:500}}>
            <h1>Municipios</h1>
            <PieChart data={chartDataMunicipios} />
          </div>
        </div>
      </div>
    </div>
  );
};

export default App;
